\r\nvar sprytextfield4 = new Spry.Widget.ValidationTextField(\"sprytextfield4\", \"none\", {validateOn:[\"blur\", \"change\"], minChars:8, maxChars:50});\r\nvar sprytextfield5 = new Spry.Widget.ValidationTextField(\"sprytextfield5\", \"none\", {validateOn:[\"blur\", \"change\"]});\r\nvar sprytextfield6 = new Spry.Widget.ValidationTextField(\"sprytextfield6\", \"none\", {validateOn:[\"blur\", \"change\"]});\r\nvar spryselect1 = new Spry.Widget.ValidationSelect(\"spryselect1\", {validateOn:[\"blur\", \"change\"], invalidValue:\"\"});\r\nvar spryselect2 = new Spry.Widget.ValidationSelect(\"spryselect2\", {validateOn:[\"blur\", \"change\"], invalidValue:\"\"});\r\nvar sprytextfield7 = new Spry.Widget.ValidationTextField(\"sprytextfield7\", \"none\", {validateOn:[\"blur\", \"change\"]});\r\nvar sprytextfield8 = new Spry.Widget.ValidationTextField(\"sprytextfield8\", \"none\", {validateOn:[\"blur\", \"change\"]});\r\nvar sprytextfield9 = new Spry.Widget.ValidationTextField(\"sprytextfield9\", \"zip_code\", {validateOn:[\"blur\"]});\r\nvar sprytextfield10 = new Spry.Widget.ValidationTextField(\"sprytextfield10\", \"phone_number\", {validateOn:[\"blur\"], useCharacterMasking:true});\r\n\r\n//-->\r\n</script>','','',0,1,'2008-04-16 13:08:32','2008-07-03 19:29:49',1,'','','',''),(30,'admin/manage/pages/edit','Administration » Manage » Pages » Edit Page','','','public','<?php\r\n\r\n	\$this->Page->Theme->Menu = \"manage\";\r\n\r\n	\$this->Page->Theme->Location = <<< EOH\r\n	\r\n<div class=\"location clear\">\r\n	<div class=\"main\">\r\n		<a href=\"{\$this->Site->URL}/admin/\" title=\"{\$this->Lang[\'title_admin\']}\">{\$this->Lang[\'title_admin\']}</a> » \r\n		<a href=\"{\$this->Site->URL}/admin/manage/\" title=\"{\$this->Lang[\'title_manage\']}\">{\$this->Lang[\'title_manage\']}</a> » \r\n		<a href=\"{\$this->Site->URL}/admin/manage/pages/\" titles=\"{\$this->Lang[\'title_pages\']}\">{\$this->Lang[\'title_pages\']}</a> » \r\n		<a href=\"{\$this->Page->RequestURL}\" titles=\"Edit Page\">Edit Page</a>\r\n	</div>\r\n</div>\r\n	\r\nEOH;\r\n\r\n	if(\$id = \$this->Request[\'id\'])\r\n	{\r\n		if(\$id === \$this->Page[\'id\'])\r\n			die();\r\n\r\n		if(\$this->Request[\'submit\'])\r\n		{\r\n			\$query = array();\r\n\r\n			\$query[] = \"`page_name` = \'\" . mysql_real_escape_string(rawurldecode(\$this->Request[\'page_name\'])) . \"\'\";\r\n			\$query[] = \"`page_title` = \'\" . mysql_real_escape_string(rawurldecode(\$this->Request[\'page_title\'])) . \"\'\";\r\n			\$query[] = \"`page_path` = \'\" . mysql_real_escape_string(preg_replace(\"#^([/]+)#\", \'\', fix_path(\"/\" . rawurldecode(\$this->Request[\'page_path\']) . \"/\"))) . \"\'\";\r\n			\$query[] = \"`page_content` = \'\" . mysql_real_escape_string(rawurldecode(\$this->Request[\'page_content\'])) . \"\'\";\r\n			\$query[] = \"`page_meta_title` = \'\" . mysql_real_escape_string(rawurldecode(\$this->Request[\'page_meta_title\'])) . \"\'\";\r\n			\$query[] = \"`page_meta_description` = \'\" . mysql_real_escape_string(rawurldecode(\$this->Request[\'page_meta_description\'])) . \"\'\";\r\n			\$query[] = \"`page_meta_keywords` = \'\" . mysql_real_escape_string(rawurldecode(\$this->Request[\'page_meta_keywords\'])) . \"\'\";\r\n			if(\$this->Request[\'theme_name\']) \$query[] = \"`theme_name` = \'{\$this->Request[\'theme_name\']}\'\";\r\n			if(\$this->Request[\'page_state\']) \$query[] = \"`page_state` = \'{\$this->Request[\'page_state\']}\'\";\r\n			if(\$this->Request[\'page_cache_time\']) \$query[] = \"`page_cache_time` = \'{\$this->Request[\'page_cache_time\']}\'\";\r\n			\$query[] = \"`updated_date` = FROM_UNIXTIME(\" . time() . \")\";\r\n			\$query[] = \"`updated_user_id` = \" . \$this->User[\'id\'];\r\n\r\n			if(\$this->Request->Validate(\"page_mime_type\", \"page_charset\"))\r\n					\$query[] = \"`page_mime_type` = \'\" . mysql_real_escape_string(\$this->Request[\'page_mime_type\']) . \"; charset=\" . mysql_real_escape_string(\$this->Request[\'page_charset\']) . \"\'\";\r\n\r\n			\$query = \r\n				\"UPDATE `{\$this->DB->Prefix}pages` \r\n				SET \" . fix_query(implode(\",\", \$query)) . \" \r\n				WHERE `page_id` = \" . \$id;\r\n\r\n			if(!\$this->DB->Query(\$query, \"master\", false))\r\n				\$this->Session[\'message\'] = 1;\r\n			else\r\n				\$this->Session[\'message\'] = 2;\r\n\r\n			\$query = \r\n				\"UPDATE `{\$this->DB->Prefix}page_permissions` \r\n				SET `permission_view` = 0 \r\n				WHERE `page_id` = \" . \$id;\r\n\r\n			\$this->DB->Query(\$query);\r\n\r\n			foreach(\$this->Request[\'group_id\'] as \$group_id)\r\n			{\r\n				\$query = \r\n					\"INSERT INTO `{\$this->DB->Prefix}page_permissions` \r\n					SET `group_id` = \" . \$group_id . \", `page_id` = \" . \$id . \", `permission_view` = 1 \r\n					ON DUPLICATE KEY UPDATE `permission_view` = 1\";\r\n\r\n				if(!\$this->DB->Query(\$query, \"master\", false))\r\n				{\r\n					\$this->Session[\'message\'] = 1;\r\n\r\n					\$this->Redirect(\$this->Page->RequestURL);\r\n				}\r\n			}\r\n\r\n			\$this->Redirect(\$this->Page->RequestURL);\r\n		}\r\n\r\n		\$query = \r\n			\"SELECT `page_name`, `page_title`, `page_path`, `page_content`, `page_meta_title`, `page_meta_description`, `page_meta_keywords`, `page_state`, `page_cache_time`, `page_mime_type`, `{\$this->DB->Prefix}pages`.`theme_name`, `{\$this->DB->Prefix}users`.`user_id`, \r\n			DATE_FORMAT(`updated_date`, \'%M %D, %Y at %l:%i %p\') as `updated_date`, `{\$this->DB->Prefix}users`.`user_username`\r\n			FROM `{\$this->DB->Prefix}pages` \r\n			LEFT JOIN `{\$this->DB->Prefix}users` ON `{\$this->DB->Prefix}pages`.`updated_user_id` = `{\$this->DB->Prefix}users`.`user_id` \r\n			WHERE `page_id` = {\$id} \r\n			LIMIT 1\";\r\n\r\n		if(\$data = \$this->DB->FetchRow(\$query, \"slave\"))\r\n		{\r\n			if(strstr(\$data[\'page_content\'], \"<?\"))\r\n				\$this->Request[\'editor\'] = \"codepress\";\r\n\r\n			if(!\$this->Request[\'editor\'])\r\n				\$this->Request[\'editor\'] = \"tinymce\";\r\n\r\n			\$data[\'page_content\'] = htmlspecialchars(\$data[\'page_content\']);\r\n			\$data[\'page_content\'] = str_replace(\"</textarea>\", \"&lt;/textarea&gt;\", \$data[\'page_content\']);\r\n			\$data[\'page_name\'] = htmlspecialchars(\$data[\'page_name\']);\r\n			\$data[\'page_title\'] = htmlspecialchars(\$data[\'page_title\']);\r\n			\$data[\'page_path\'] = htmlspecialchars(\$data[\'page_path\']);\r\n			\$data[\'page_meta_title\'] = htmlspecialchars(\$data[\'page_meta_title\']);\r\n			\$data[\'page_meta_description\'] = htmlspecialchars(\$data[\'page_meta_description\']);\r\n			\$data[\'page_meta_keywords\'] = htmlspecialchars(\$data[\'page_meta_keywords\']);\r\n			\$data[\'page_mime_type\'] = htmlspecialchars(\$data[\'page_mime_type\']);\r\n\r\n			switch(\$this->Session[\'message\'])\r\n			{\r\n				case 1: \r\n?>\r\n\r\n<div class=\"error\">Edit failed.</div>\r\n\r\n<?php\r\n				break;\r\n				case 2: \r\n?>\r\n\r\n<div class=\"success\">Edit successful.</div>\r\n\r\n<?php\r\n				break;\r\n			}\r\n\r\n			unset(\$this->Session[\'message\']);\r\n\r\n			if(\$this->Request[\'editor\'] === \"codepress\")\r\n			{\r\n?>\r\n				<form action=\"\" method=\"post\" onsubmit=\"editor.saveCode();\">\r\n<?php\r\n			}\r\n			else\r\n			{\r\n?>\r\n				<form action=\"\" method=\"post\">\r\n<?php\r\n			}\r\n?>\r\n\r\n	<input type=\"hidden\" name=\"page_path\" value=\"<?=\$data[\'page_path\']?>\" />\r\n\r\n	<table cellpadding=\"0\" cellspacing=\"5\" border=\"0\" style=\"width: 100%\">\r\n		<tr>\r\n			<td style=\"width: 175px\">\r\n				<h4><strong>Name</strong><span class=\"formInfo\"><a class=\"jTip\" id=\"tt10\" title=\"<?=\$this->Lang[\'title_help\']?>\" rel=\"Easily track and manage each page within the CCMS system.\">?</a></span><br class=\"clear\" /></h4>\r\n			</td>\r\n			<td>\r\n				<input type=\"text\" maxlength=\"2048\" size=\"40\" name=\"page_name\" class=\"reset\" style=\"border: 1px solid #DDD;padding: 6px\" value=\"<?=\$data[\'page_name\']?>\" />\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<h4><strong>Title</strong><span class=\"formInfo\"><a class=\"jTip\" id=\"tt1\" title=\"<?=\$this->Lang[\'title_help\']?>\" rel=\'The title area within the head tag plus the \"Title\" under \"Administration » Configure » General Settings\" of CCMS.\'>?</a></span><br class=\"clear\" /></h4>\r\n			</td>\r\n			<td>\r\n				<input type=\"text\" maxlength=\"2048\" size=\"40\" name=\"page_title\" class=\"reset\" style=\"border: 1px solid #DDD;padding: 6px\" value=\"<?=\$data[\'page_title\']?>\" />\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<h4><strong>URL <span class=\"red\">*</span></strong><span class=\"formInfo\"><a class=\"jTip\" id=\"tt2\" title=\"<?=\$this->Lang[\'title_help\']?>\" rel=\'Will auto-fill with what you type within the \"Name\" field, so keep in mind stay on target and keyword enriched. If you want to define your own url, click the page path generated.\'>?</a></span><br class=\"clear\" /></h4>\r\n			</td>\r\n			<td>\r\n				<?=\$this->Site->URL?>/<div class=\"path\" style=\"display: inline\"><?=\$data[\'page_path\']?></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td class=\"fix\">\r\n				<h4><strong>Group Permissions</strong><span class=\"formInfo\"><a class=\"jTip\" id=\"tt12\" title=\"Help\" rel=\'Choose the groups which should have permission to view this page.\'>?</a></span><br class=\"clear\" /></h4>\r\n			</td>\r\n			<td>\r\n				<select size=\"5\" multiple=\"multiple\" class=\"dropdown\" name=\"group_id[]\">\r\n<?php\r\n\r\n	\$query = \r\n		\"SELECT `group_id`, `permission_view` \r\n		FROM `{\$this->DB->Prefix}page_permissions`\r\n		WHERE `page_id` = \" . \$id;\r\n\r\n	\$permissions = array();\r\n\r\n	foreach(\$this->DB->FetchRows(\$query, \"slave\") as \$permission)\r\n	{\r\n		\$permissions[\$permission[\'group_id\']] = \$permission;\r\n	}\r\n\r\n	\$query = \r\n		\"SELECT `group_id`, `group_title` \r\n		FROM `{\$this->DB->Prefix}groups`\";\r\n\r\n	foreach(\$this->DB->FetchRows(\$query, \"slave\") as \$row)\r\n	{\r\n		if(\$permissions[\$row[\'group_id\']][\'permission_view\'] === \"1\")\r\n		{\r\n?>\r\n\r\n<option value=\"<?=\$row[\'group_id\']?>\" selected=\"\"><?=\$row[\'group_title\']?></option>\r\n\r\n<?php\r\n		}\r\n		else\r\n		{\r\n?>\r\n\r\n<option value=\"<?=\$row[\'group_id\']?>\"><?=\$row[\'group_title\']?></option>\r\n\r\n<?php\r\n		}\r\n	}\r\n?>\r\n				</select>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n\r\n			</td>\r\n			<td>\r\n				<strong>>> <a href=\"javascript:;\" class=\"slideDownLink fix\" rel=\"advancedOptions\">Advanced Options</a></strong>\r\n				\r\n				<table class=\"slideDownBox\" style=\"display: none\" cellpadding=\"0\" cellspacing=\"5\" border=\"0\">\r\n					<tr>\r\n						<td style=\"width: 150px\">\r\n							<strong>Options</strong>\r\n						</td>\r\n						<td>\r\n							<select name=\"theme_name\">\r\n								<option value=\"\">--- Select Theme ---</option>\r\n<?php\r\n	\$themes = \'\';\r\n\r\n	\$theme_list = list_children_folders(\$this->Site->Path . \"/themes/\");\r\n\r\n	natcasesort(\$theme_list);\r\n\r\n	foreach(\$theme_list as \$theme)\r\n	{\r\n		\$themes .= \"<option value=\'{\$theme}\'>{\$theme}</option>\";\r\n	}\r\n\r\n	echo str_replace(\"value=\'{\$data[\'theme_name\']}\'\", \"value=\'{\$data[\'theme_name\']}\' selected=\'selected\'\", \$themes);\r\n\r\n?>\r\n							</select>\r\n							 \r\n							<select name=\"page_mime_type\" style=\"display: none\">\r\n								<option value=\"\">--- Select Mime-Type ---</option>\r\n								<option value=\"text/html\">text/html</option>\r\n								<option value=\"text/plain\">text/plain</option>\r\n								<option value=\"text/javascript\">text/javascript</option>\r\n								<option value=\"text/css\">text/css</option>\r\n								<option value=\"text/xml\">text/xml</option>\r\n								<option value=\"other\">other</option>\r\n							</select>\r\n							 \r\n							<select name=\"page_charset\" style=\"display: none\">\r\n								<option value=\"\">--- Select Charset ---</option>\r\n								<option value=\"iso-8859-1\">iso-8859-1</option>\r\n								<option value=\"utf-8\">utf-8</option>\r\n							</select>\r\n							 \r\n							<select name=\"page_state\" style=\"display: none\">\r\n								<option value=\"\">--- Select State ---</option>\r\n								<option value=\"Draft\">Draft</option>\r\n								<option value=\"Public\">Public</option>\r\n								<option value=\"Private\">Private</option>\r\n								<option value=\"Closed\">Closed</option>\r\n							</select>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td>\r\n							<strong>Meta Title</strong>\r\n						</td>\r\n						<td>\r\n							<input type=\"text\" maxlength=\"2048\" size=\"40\" name=\"page_meta_title\" class=\"reset\" style=\"border: 1px solid #DDD;padding: 6px\" value=\"<?=\$data[\'page_meta_title\']?>\" />\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td>\r\n							<strong>Meta Description</strong>\r\n						</td>\r\n						<td>\r\n							<textarea name=\"page_meta_description\" rows=\"2\" cols=\"100\"><?=\$data[\'page_meta_description\']?></textarea>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td>\r\n							<strong>Meta Keywords</strong>\r\n						</td>\r\n						<td>\r\n							<textarea name=\"page_meta_keywords\" rows=\"2\" cols=\"100\"><?=\$data[\'page_meta_keywords\']?></textarea>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td>\r\n							<strong>Cache Time</strong>\r\n						</td>\r\n						<td>\r\n							<input type=\"text\" name=\"page_cache_time\" value=\"<?=\$data[\'page_cache_time\']?>\" />\r\n						</td>\r\n					</tr>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td class=\"fix\">\r\n				<h4><strong>Content</strong><span class=\"formInfo\"><a class=\"jTip\" id=\"tt3\" title=\"<?=\$this->Lang[\'title_help\']?>\" rel=\"Will be the main section of what your page is about.\">?</a></span><br class=\"clear\" /></h4>\r\n				<br />\r\n				<div style=\"-moz-border-radius-topleft:3px;-moz-border-radius-topright:3px;background-color:#4B7FC4;color:#FFF;padding:10px;\">\r\n					<select id=\"editorChooser\" style=\"width: 150px\">\r\n<?php\r\n\r\n\$editors = <<< EOH\r\n\r\n<option value=\'\'>--- Choose Editor ---</option>\r\n<option value=\'tinymce\'>TinyMCE</option>\r\n<option value=\'codepress\'>CodePress</option>\r\n\r\nEOH;\r\n\r\necho str_replace(\"value=\'{\$this->Request[\'editor\']}\'\", \"value=\'{\$this->Request[\'editor\']}\' selected=\'selected\'\", \$editors);\r\n\r\n?>\r\n					</select><span class=\"formInfo\" style=\"display: none\"><a class=\"jTip\" style=\"font-weight: 1.4em !important\" id=\"tt13\" title=\"<?=\$this->Lang[\'title_help\']?>\" rel=\"Choose the type of editor you would like to use. TinyMCE is for design. CodePress is for coding.\">?</a></span><br class=\"clear\" />\r\n				</div>\r\n			</td>\r\n			<td>\r\n<?php\r\n			if(\$this->Request[\'editor\'] === \"codepress\")\r\n			{\r\n?>\r\n				<textarea id=\"editor\" class=\"reset codepress php\" name=\"page_content\" wrap=\"none\" style=\"height: 400px\"><?=\$data[\'page_content\']?></textarea>\r\n<?php\r\n			}\r\n			else\r\n			{\r\n?>\r\n				<textarea id=\"editor2\" name=\"page_content\" wrap=\"none\" class=\"reset mceEditor\" style=\"width: 100%\"><?=\$data[\'page_content\']?></textarea>\r\n<?php\r\n			}\r\n?>\r\n			</td>\r\n		</tr>\r\n	</table>\r\n	<br />\r\n	<?php printf(\$this->Lang[\'last_edited\'], \"<a href=\'{\$this->Site->URL}/admin/manage/users/edit/{\$data[\'user_id\']}/\'>{\$data[\'user_username\']}</a>\", \$data[\'updated_date\']); ?>\r\n	<br />\r\n	<div style=\"float: right\">\r\n		<input class=\"reset submit preview\" type=\"button\" name=\"preview\" value=\"Preview\" /> <input class=\"reset submit\" type=\"submit\" name=\"submit\" value=\"Save\" />\r\n	</div>\r\n</form>\r\n<br /><br />\r\n<iframe id=\"page_preview\" width=\"100%\" height=\"400px\" border=\"0\" frameborder=\"0\"></iframe>\r\n<br /><br /><br /><br /><br />\r\n\r\n<script type=\"text/javascript\">\r\n	jQuery(document).ready(function(\$)\r\n	{\r\n		\$(\".slideDownLink\").click(function()\r\n		{\r\n			\$(\".slideDownBox\").toggle();\r\n		});\r\n\r\n		\$(\"input[name=\'page_title\']\").keyup(function()\r\n		{\r\n			var title = \$(this).val();\r\n\r\n			var path = (title.toLowerCase()).replace(/ /g, \"-\");\r\n\r\n			var form = \$(this).parents(\"form:first\");\r\n\r\n			form.find(\"input[name=\'page_path\']\").val(path);\r\n			form.find(\".path\").html(path);\r\n		});\r\n\r\n		\$(\".path\").click(function()\r\n		{\r\n			if(this.editing != true)\r\n			{\r\n				var self = this;\r\n				var \$self = \$(this);\r\n\r\n				self.editing = true;\r\n				var path = \$self.html();\r\n\r\n				\$self.html(\"<input class=\'edit-path\' type=\'text\' value=\'\" + path + \"\' class=\'reset\' style=\'display: inline\' /> <a href=\'\' class=\'save\'>Save</a> <a href=\'\' class=\'cancel\'>Cancel</a>\");\r\n				\$(\".save\", self).click(function()\r\n				{\r\n					var path = \$(\".edit-path\", self).val();\r\n\r\n					\$self.html(path);\r\n\r\n					\$self.parents(\"form:first\").find(\"input[name=\'page_path\']\").val(path);\r\n\r\n					self.editing = false;\r\n\r\n					return false;\r\n				});\r\n\r\n				\$(\".cancel\", self).click(function()\r\n				{\r\n					\$self.html(path);\r\n\r\n					self.editing = false;\r\n\r\n					return false;\r\n				});\r\n			}\r\n\r\n			return false;\r\n		});\r\n\r\n		\$(\".preview\").click(function()\r\n		{\r\n<?php\r\n			if(\$this->Request[\'editor\'] === \"codepress\")\r\n			{\r\n?>\r\n\r\n\r\n<?php\r\n			}\r\n			else\r\n			{\r\n?>\r\n				tinyMCE.activeEditor.save();\r\n<?php\r\n			}\r\n?>\r\n\r\n			formData = \$(this).parents(\"form:first\").serialize();\r\n\r\n\r\n<?php\r\n			if(\$this->Request[\'editor\'] === \"codepress\")\r\n			{\r\n?>\r\n				formData = formData + \"&cache=1&page_content=\" + escape(editor.getCode());\r\n<?php\r\n			}\r\n?>\r\n\r\n			\$.post(\"<?=\$this->Site->URL?>/admin/page/preview/\", formData, function(data)\r\n			{\r\n				\$(\"#page_preview\")[0].contentWindow.document.body.innerHTML = data;\r\n			});\r\n		});\r\n\r\n		\$(\"#editorChooser\").change(function()\r\n		{\r\n			window.location = \"<?=\$this->Page->FullURL?>/?editor=\" + \$(this).val();\r\n		});\r\n	});\r\n</script>\r\n\r\n<script src=\"<?=\$this->Site->URL?>/libs/codepress/codepress.js\" type=\"text/javascript\"></script>\r\n<script language=\"javascript\" type=\"text/javascript\" src=\"<?=\$this->Site->URL?>/libs/tiny_mce/tiny_mce.js\"></script>\r\n<script language=\"javascript\" type=\"text/javascript\">\r\n	tinyMCE.init({\r\n		mode : \"specific_textareas\",\r\n		editor_selector : \"mceEditor\",\r\n		theme : \"advanced\",\r\n		plugins : \"safari,spellchecker,style,layer,table,save,advhr,advimage,advlink,emotions,iespell,inlinepopups,insertdatetime,preview,media,searchreplace,print,contextmenu,paste,directionality,fullscreen,noneditable,visualchars,nonbreaking,xhtmlxtras,template,pagebreak\",\r\n		theme_advanced_buttons1_add_before : \"newdocument,separator\",\r\n		theme_advanced_buttons1_add : \"fontselect,fontsizeselect\",\r\n		theme_advanced_buttons2_add : \"separator,insertdate,inserttime,preview,separator,forecolor,backcolor\",\r\n		theme_advanced_buttons2_add_before: \"cut,copy,paste,pastetext,pasteword,separator,search,replace,separator\",\r\n		theme_advanced_buttons3_add_before : \"tablecontrols,separator\",\r\n		theme_advanced_buttons3_add : \"emotions,iespell,media,advhr,separator,print,separator,ltr,rtl,separator,fullscreen\",\r\n		theme_advanced_buttons4 : \"insertlayer,moveforward,movebackward,absolute,|,styleprops,|,spellchecker,cite,abbr,acronym,del,ins,attribs,|,visualchars,nonbreaking,template,blockquote,pagebreak,|,insertfile,insertimage\",\r\n		theme_advanced_toolbar_location : \"top\",\r\n		theme_advanced_toolbar_align : \"left\",\r\n		theme_advanced_statusbar_location : \"bottom\",\r\n		plugin_insertdate_dateFormat : \"%Y-%m-%d\",\r\n		plugin_insertdate_timeFormat : \"%H:%M:%S\",\r\n		theme_advanced_resize_horizontal : false,\r\n		theme_advanced_resizing : true,\r\n		//relative_urls : false,\r\n		//remove_script_host : false,\r\n		valid