r\n<?php\r\n\r\n	\$settings = &\$this->LoadSetting(\"modules\");\r\n\r\n	foreach(\$settings as \$module_name => \$setting)\r\n	{\r\n		if(\$setting[\'enabled\'] == 1)\r\n			\$enabled = true;\r\n		else\r\n			\$enabled = false;\r\n\r\n		\$path = \$this->Site->Path . \"/modules/\" . \$module_name . \"/module.xml\";\r\n\r\n		if(\$xml = get_file(\$path))\r\n		{\r\n			\$module = simplexml_load_string(\$xml);\r\n			\r\n			\$title = \$module->title;\r\n			\$description = \$module->description;\r\n		}\r\n		else\r\n		{\r\n			\$title = \$module_name;\r\n			\$description = \$this->Lang[\'no_description\'];\r\n		}\r\n		\r\n?>\r\n	<div class=\"box-1\">\r\n		<input type=\"hidden\" name=\"modules[<?=\$module_name?>][enabled]\" value=\"<?php if(\$enabled) echo 1; else echo 0; ?>\" />\r\n		<div class=\"state <?php if(!\$enabled) echo \"disabled\"; ?>\">\r\n			<div class=\"toggle\"></div>\r\n			<h3>\r\n				<span><?=\$title?></span>\r\n				<a class=\"up\" href=\"\"><img src=\"<?=\$this->Site->URL?>/images/module-up.png\" /></a>\r\n				<a class=\"down\" href=\"\"><img src=\"<?=\$this->Site->URL?>/images/module-down.png\" /></a>\r\n				<a href=\"<?=\$this->Page->URL?>/configure/<?=\$module_name?>/\" title=\"Configure\">Configure</a>\r\n<?php \r\n	if(\$enabled)\r\n	{\r\n?>\r\n	<a class=\"disable\" rel=\"<?=\$module_name?>\" href=\"\" title=\"Disable\">Disable</a>\r\n<?php\r\n	}\r\n	else\r\n	{\r\n?>\r\n	<a class=\"enable\" rel=\"<?=\$module_name?>\" href=\"\" title=\"Enable\">Enable</a>\r\n<?php\r\n	}\r\n?>\r\n				<br />\r\n			</h3>\r\n			<div class=\"inside\">\r\n				<div class=\"description\">\r\n					<?=\$description?>\r\n<?php\r\n	if(\$module)\r\n	{\r\n?>\r\n	<br /><br /><strong>Author:</strong> <a href=\"<?=\$module->url?>\"><?=\$module->author?></a>\r\n<?php\r\n	}\r\n?>\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n\r\n<?php\r\n	}\r\n\r\n	\$modules = list_children_folders(PATH . \"/modules/\");\r\n		\r\n	foreach(\$modules as \$module_name)\r\n	{\r\n		if(!array_key_exists(\$module_name, \$settings))\r\n		{\r\n			if(\$settings[\$module_name][\'enabled\'] == 1)\r\n				\$enabled = true;\r\n			else\r\n				\$enabled = false;\r\n\r\n			\$path = \$this->Site->Path . \"/modules/\" . \$module_name . \"/module.xml\";\r\n\r\n			if(\$xml = get_file(\$path))\r\n			{\r\n				\$module = simplexml_load_string(\$xml);\r\n				\r\n				\$title = \$module->title;\r\n				\$description = \$module->description;\r\n			}\r\n			else\r\n			{\r\n				\$title = \$module_name;\r\n				\$description = \$this->Lang[\'no_description\'];\r\n			}\r\n		\r\n?>\r\n	<div class=\"box-1\">\r\n		<input type=\"hidden\" name=\"modules[<?=\$module_name?>][enabled]\" value=\"<?php if(\$enabled) echo 1; else echo 0; ?>\" />\r\n		<div class=\"state <?php if(!\$enabled) echo \"disabled\"; ?>\">\r\n			<div class=\"toggle\"></div>\r\n			<h3>\r\n				<span><?=\$title?></span>\r\n				<a class=\"up\" href=\"\"><img src=\"<?=\$this->Site->URL?>/images/module-up.png\" /></a>\r\n				<a class=\"down\" href=\"\"><img src=\"<?=\$this->Site->URL?>/images/module-down.png\" /></a>\r\n				<a href=\"<?=\$this->Page->URL?>/configure/<?=\$module_name?>/\" title=\"Configure\">Configure</a>\r\n<?php \r\n			if(\$enabled)\r\n			{\r\n?>\r\n	<a class=\"disable\" href=\"\" title=\"Disable\">Disable</a>\r\n<?php\r\n			}\r\n			else\r\n			{\r\n?>\r\n	<a class=\"enable\" href=\"\" title=\"Enable\">Enable</a>\r\n<?php\r\n			}\r\n?>\r\n				<br />\r\n			</h3>\r\n			<div class=\"inside\">\r\n				<div class=\"description\">\r\n					<?=\$description?>\r\n<?php\r\n			if(\$module)\r\n			{\r\n?>\r\n	<br /><br /><strong>Author:</strong> <a href=\"<?=\$module->url?>\"><?=\$module->author?></a>\r\n<?php\r\n			}\r\n?>\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n\r\n<?php\r\n		}\r\n	}\r\n?>\r\n</form>\r\n<br /><br /><br /><br /><br />\r\n\r\n<script type=\"text/javascript\" src=\"<?=\$this->Site->URL?>/js/jquery.json.js\"></script>\r\n<script type=\"text/javascript\">\r\n	jQuery(document).ready(function(\$)\r\n	{\r\n		\$(\"a.up\").click(function()\r\n		{\r\n			var parentBox = \$(this).parents(\".box-1:first\");\r\n			var prevBox = parentBox.prev(\".box-1\");\r\n\r\n			if(prevBox.length > 0)\r\n			{\r\n				parentBox.clone(true).insertBefore(prevBox);\r\n				parentBox.remove();\r\n			}\r\n		\r\n			return false;\r\n		});\r\n\r\n		\$(\"a.down\").click(function()\r\n		{\r\n			var parentBox = \$(this).parents(\".box-1:first\");\r\n			var nextBox = parentBox.next(\".box-1\");\r\n\r\n			if(nextBox.length > 0)\r\n			{\r\n				parentBox.clone(true).insertAfter(nextBox);\r\n				parentBox.remove();\r\n			}\r\n\r\n			return false;\r\n		});\r\n\r\n		\$(\"div.box-1 h3\").click(function()\r\n		{\r\n			var self = \$(this);\r\n\r\n			var box = self.parents(\".box-1:first\");\r\n			var state = box.children(\".state:first\");\r\n			var inside = state.children(\".inside:first\");\r\n\r\n			state.toggleClass(\"opened\");\r\n		}).stopSelection();\r\n\r\n		var enable = function()\r\n		{\r\n			\$(this).parents(\"form:first\").find(\"input[name=\'modules[\" + \$(this).attr(\"rel\") + \"][enabled]\']\").val(\"1\");\r\n\r\n			\$(this).parents(\".state:first\").removeClass(\"disabled\");\r\n\r\n			\$(this).html(\"Disable\").attr(\"title\", \"Disable\");\r\n\r\n			\$(this).unbind(\"click\").bind(\"click\", disable);\r\n\r\n			return false;\r\n		};\r\n\r\n		var disable = function()\r\n		{\r\n			\$(this).parents(\"form:first\").find(\"input[name=\'modules[\" + \$(this).attr(\"rel\") + \"][enabled]\']\").val(\"0\");\r\n\r\n			\$(this).parents(\".state:first\").addClass(\"disabled\");\r\n\r\n			\$(this).html(\"Enable\").attr(\"title\", \"Enable\");\r\n\r\n			\$(this).unbind(\"click\").bind(\"click\", enable);\r\n\r\n			return false;\r\n		};\r\n\r\n		\$(\".disable\").bind(\"click\", disable);\r\n\r\n		\$(\".enable\").bind(\"click\", enable);\r\n\r\n		\$(\".save\").click(function()\r\n		{\r\n//\"JSON=\" + escape(\$.toJSON(modules)) + \"&AJAX=1\"\r\n			if(confirm(\"Are you sure you want to this configuration?\\n\\nDisabling or changing the order of database/core/cms modules may cause everything to stop working.\"))\r\n			{\r\n				\$.post(\"<?=\$this->Page->RequestURL?>\", \$(this).parents(\"form:first\").serialize() + \"&AJAX=1\", function(data)\r\n				{\r\n					var \$message = \$(\"#message\");\r\n\r\n					\$message.hide();\r\n\r\n					if(data.status == 1)\r\n						\$message.addClass(\"success\");\r\n					else\r\n						\$message.addClass(\"error\");\r\n\r\n					\$message.html(data.message).fadeIn(500, function()\r\n					{\r\n						setTimeout(function()\r\n						{\r\n							\$message.fadeOut(500, function()\r\n							{\r\n								\$message.html(\'\');\r\n							});\r\n						}, 2000);\r\n					});\r\n				}, \"json\");\r\n			}\r\n\r\n			return false;\r\n		});\r\n\r\n	});\r\n</script>','admin','',0,0,'0000-00-00 00:00:00','2008-07-07 09:30:23',0,'','','',''),(83,'account/password/change','Account > Change Password','','','public','<?php\n\n	\$this->Page->Theme->Menu = \"account\";\n\n?>\n\n<h2>Change Password</h2>\n<br />\n\n<?php\n\n	if(\$this->Request[\'submit\'])\n	{\n		if(!\$this->Request[\'password_old\'])\n			\$message = \"<div class=\'error\'>Please enter your old password.</div>\";\n		else if(!\$this->Request->ValidateSQL(\"password_old\"))\n			\$message = \"<div class=\'error\'>Please enter a valid old password.</div>\";\n		else if(!\$this->DB->FetchRow(\"SELECT `user_password` FROM `{\$this->DB->Prefix}users` WHERE `user_username` = \'{\$this->Session[\'username\']}\' AND `user_password` = \'\" . md5(\$this->Request[\'password_old\']) . \"\' LIMIT 1\", \"slave\"))\n			\$message = \"<div class=\'error\'>Your old password was incorrect.</div>\";\n		else if(!\$this->Request[\'password_new\'])\n			\$message = \"<div class=\'error\'>Please enter your new password.</div>\";\n		else if(!\$this->Request->ValidateSQL(\"password_new\"))\n			\$message = \"<div class=\'error\'>Please enter a valid new password.</div>\";\n		else if(!\$this->Request[\'password_new_confirmation\'])\n			\$messa